update bfx_impl.declaration_main_questions
   set item_text = N'Страхователь подтверждает, что он не является налоговым резидентом США и не обладает ни одним из признаков налогового резидента США (не имеет гражданства США (в том числе в случае наличия двух и более гражданств) и разрешения на постоянное пребывание в США (форма I-551 Green Card); не соответствует критерию «Долгосрочное пребывание» на территории США; местом рождения Страхователя не является территория США; не имеет адреса проживания и/или почтового адреса, номера контактного телефона и/или факса на территории США; не имеет открытых счетов в кредитных организациях на территории США). (Физическое лицо соответствует критерию «Долгосрочное пребывание», если оно находилось на территории США не менее 31 дня в течение текущего календарного года и не менее 183 дней в течение 3 лет, включая текущий год и два непосредственно предшествующих года. При этом сумма дней, в течение которых физическое лицо присутствовало на территории США в текущем году, а также двух предшествующих годах, умножается на установленный коэффициент: коэффициент для текущего года равен 1 (т. е. учитываются все дни, проведенные в США в текущем году); коэффициент предшествующего года равен 1/3; коэффициент позапрошлого года – 1/6)'
 where id = '4d28f96b-3b6d-4fcd-93b2-2f1a43497d11'

