-- set first symbol to lower and remove last symbol "."
update bfx_impl.declaration_main_questions
   set item_text = lower(substring(item_text, 1, 1)) + substring(item_text, 2, len(item_text) - 2)
 where substring(item_text, len(item_text), 1) = '.'
-- set first symbol to upper if it's 'С' or 'З'
update bfx_impl.declaration_main_questions
   set item_text = upper(substring(item_text, 1, 1)) + substring(item_text, 2, len(item_text))
 where substring(item_text, 1, 1) = N'С' or substring(item_text, 1, 1) = N'З'

-- set first symbol to lower and remove last symbol "."
update bfx_impl.declaration_medical_questions
   set item_text = lower(substring(item_text, 1, 1)) + substring(item_text, 2, len(item_text) - 2)
 where substring(item_text, len(item_text), 1) = '.'
-- set first symbol to upper if it's 'С' or 'З'
update bfx_impl.declaration_medical_questions
   set item_text = upper(substring(item_text, 1, 1)) + substring(item_text, 2, len(item_text))
 where substring(item_text, 1, 1) = N'С' or substring(item_text, 1, 1) = N'З'