update bfx_impl.declaration_main_questions
   set item_text = N'Общая страховая сумма в отношении Застрахованного по риску «Смерть Застрахованного в результате несчастного случая» по всем договорам страхования ООО СК «Росгосстрах Жизнь» по продуктам инвестиционного страхования жизни и продуктам, договоры страхования по которым заключаются на основании Правил страхования жизни (в редакции от любого числа), включая настоящий договор страхования не превышает 20 000 000 рублей для Застрахованных в возрасте от 18 до 65 полных лет (включительно) на дату заключения договора страхования (15 000 000 рублей для Застрахованных в возрасте от 66 до 70 полных лет (включительно) на дату заключения договора страхования; 5 000 000 рублей для Застрахованных в возрасте от 71 года до 75 полных лет (включительно) на дату заключения договора страхования).'
