delete from bfx_impl.declaration_main_questions where id = 'DAD4C660-C041-452B-8412-78ADA4B5193C'; insert into bfx_impl.declaration_main_questions values ('DAD4C660-C041-452B-8412-78ADA4B5193C', N'Общая страховая сумма в отношении Застрахованного по всем договорам страхования, заключенным с ООО СК «Росгосстрах Жизнь», включая настоящий договор страхования, не превышает:
