delete from bfx_impl.declaration_main_questions where id = '9B136E12-9626-465A-9163-5DD999A5614D'; insert into bfx_impl.declaration_main_questions values ('9B136E12-9626-465A-9163-5DD999A5614D', N'На основании положений Федерального закона от 21.11.2011 № 323-ФЗ «Об основах охраны здоровья граждан Российской Федерации» Застрахованный разрешает медицинским и иным учреждениям предоставлять информацию о состоянии его здоровья, о фактах обращения за оказанием медицинской помощи, диагнозе, иные сведения, полученные при медицинском обследовании и лечении Застрахованного, составляющие врачебную тайну, в том числе предоставление соответствующих копий документов, по запросу Страховщика.');
