insert into bfx_impl.declaration_medical_questions values ('B6D537C0-6368-451B-99A8-C93AA6763588', N'Не имел(а), не обращался (не обращалась) за медицинской помощью по нижеперечисленным заболеваниям и не страдаю ими в настоящее время: 
