delete from bfx_impl.declaration_medical_questions where id = 'F186D984-4C9C-41FE-A7CE-2ADA244C440A'; insert into bfx_impl.declaration_medical_questions values ('F186D984-4C9C-41FE-A7CE-2ADA244C440A', N'не имел(а), не обращался (не обращалась) за медицинской помощью по нижеперечисленным заболеваниям и не страдаю ими в настоящее время:
