update BFX_IMPL.RISK_PRODUCT_RELATION
  set RISK_CODE = 'DNS36404' where PRODUCT_CODE in('IBAP3VTB', 'IBAP5VTB', 'IBAV3VTB', 'IBAV5VTB') AND  RISK_CODE = 'DNS36904'

update BFX_IMPL.DECLARATION_MAIN
  set DEPARTAMENT = 'compliance' where PRODUCT_CODE in('IBAP3VTB', 'IBAP5VTB', 'IBAV3VTB', 'IBAV5VTB') AND  ITEM_NUMBER = '2'

delete from bfx_impl.declaration_medical_questions where id = 'E6F17DA0-3E43-44B6-B211-5B989F6FAF7D'; insert into bfx_impl.declaration_medical_questions values ('E6F17DA0-3E43-44B6-B211-5B989F6FAF7D', N'Не являюсь и не являлся (не являлась) инвалидом I группы и не подавал(а) документы на освидетельствование для получения группы инвалидности, не имел(а), не проходил(а) лечения и не страдаю в настоящее время от онкологических заболеваний, болезней сердечно-сосудистой системы (инфаркт миокарда, инсульт, стенокардия, порок сердца, тромбоз, сердечно-сосудистая недостаточность), каких-либо заболеваний головного мозга, гепатита B и C, цирроза печени, сахарного диабета I типа; неврологических или психических расстройств (эпилепсия, паралич, алкоголизм, наркомания), СПИД или ВИЧ-инфекции.');
delete from bfx_impl.declaration_medical_questions where id = '17C01783-37FC-4AEA-B517-5C814F5F28DE'; insert into bfx_impl.declaration_medical_questions values ('17C01783-37FC-4AEA-B517-5C814F5F28DE', N'Не употребляю и не употреблял(а) ранее наркотики, токсические вещества, не страдаю алкоголизмом, не состою и не состоял (не состояла) ранее на учете в наркологическом, психоневрологическом диспансере, центрах профилактики борьбы со СПИДом.');
