insert into  [BFX_IMPL].[DECLARATION_MAIN_QUESTIONS] values ('8B50DFA7-B703-4E89-AFA1-CF6FC6846B31',N'Страхователь подтверждает, что Ключевой информационный документ об условиях договора добровольного страхования до заключения договора страхования им получен, с условиями Ключевого информационного документа он ознакомлен и согласен.')


